    private void button1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button1ActionPerformed
        //get user input
        int fahr1 = Integer.parseInt(text1.getText());
        // subract 32 from number
        double farh2 = fahr1 - 32;
        //multiply by .5556
        double farh3 = farh2 * 5;
        // divide input by 9
        double farh4 = farh3 / 9;
        // show answer to user
        text2.setText(Double.toString(farh4));
    }//GEN-LAST:event_button1ActionPerformed

