    private void button2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button2ActionPerformed
        //get user input
        int cels1 = Integer.parseInt(text2.getText());
        //multiply user input by 9
        double cels2 = cels1 * 9;
        // divide number by 5
        double cels3 = cels2 / 5;
        // add 32 from number
        double cels4 = cels3 + 32;
        // show answer to user
        text1.setText(Double.toString(cels4));
    }//GEN-LAST:event_button2ActionPerformed

